import React, { useState, useEffect } from "react";

export default function BookingManager() {
  const [bookings, setBookings] = useState([]);
  const [pupils, setPupils] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null); // { bookingId, field }
  const BOOKINGS_URL = "http://localhost:3001/api/v1/bookings";
  const PUPILS_URL = "http://localhost:3001/api/v1/pupils";

  // Fetch bookings (server filters to today)
  const fetchBookings = async () => {
    try {
      const res = await fetch(BOOKINGS_URL);
      const data = await res.json();
      setBookings(data);
    } catch (err) {
      console.error("Error fetching bookings:", err);
    }
  };

  // Fetch pupils
  const fetchPupils = async () => {
    try {
      const res = await fetch(PUPILS_URL);
      const data = await res.json();
      setPupils(data);
    } catch (err) {
      console.error("Error fetching pupils:", err);
    }
  };

  useEffect(() => {
    fetchBookings();
    fetchPupils();
  }, []);

  // Update booking slot with selected pupil
  const handleSelectPupil = async (pupilName) => {
    if (!selectedSlot) return;
    try {
      const res = await fetch(`${BOOKINGS_URL}/${selectedSlot.bookingId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ [selectedSlot.field]: pupilName }),
      });
      if (res.ok) {
        setSelectedSlot(null);
        fetchBookings();
      }
    } catch (err) {
      console.error("Error updating booking:", err);
    }
  };

  return (
    <div>
      <h1>Today's Bookings</h1>

      <ul>
        {bookings.map((booking) => (
          <li key={booking._id}>
            <div>
              <button
                onClick={() =>
                  setSelectedSlot({ bookingId: booking._id, field: "first" })
                }
              >
                {booking.first || "First Slot"}
              </button>
              <button
                onClick={() =>
                  setSelectedSlot({ bookingId: booking._id, field: "second" })
                }
              >
                {booking.second || "Second Slot"}
              </button>
              <button
                onClick={() =>
                  setSelectedSlot({ bookingId: booking._id, field: "third" })
                }
              >
                {booking.third || "Third Slot"}
              </button>
            </div>
          </li>
        ))}
      </ul>

      {/* Show pupil selection if a slot is clicked */}
      {selectedSlot && (
        <div style={{ marginTop: "20px" }}>
          <h2>Select Pupil for {selectedSlot.field} Slot</h2>
          {pupils.map((pupil) => (
            <button
              key={pupil._id}
              onClick={() => handleSelectPupil(pupil.name)}
              style={{ margin: "5px" }}
            >
              {pupil.name}
            </button>
          ))}
          <div>
            <button onClick={() => setSelectedSlot(null)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
}
