import { useState, useEffect } from "react";

export default function BookingApp() {
  const [bookings, setBookings] = useState([]);
  const [form, setForm] = useState({ name: "", date: "", timeSlot: "" });

  // Create booking
  const createBooking = async () => {
    await fetch("http://localhost:5000/bookings", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    setForm({ name: "", date: "", timeSlot: "" });
    getBookings();
  };

  // Read all bookings
  const getBookings = async () => {
    const res = await fetch("http://localhost:5000/bookings");
    const data = await res.json();
    setBookings(data);
  };

  // Update booking
  const updateBooking = async (id) => {
    await fetch(`http://localhost:5000/bookings/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    getBookings();
  };

  // Delete booking
  const deleteBooking = async (id) => {
    await fetch(`http://localhost:5000/bookings/${id}`, { method: "DELETE" });
    getBookings();
  };

  useEffect(() => {
    getBookings();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Booking Time Slots</h2>
      <input
        placeholder="Name"
        value={form.name}
        onChange={(e) => setForm({ ...form, name: e.target.value })}
      />
      <input
        type="date"
        value={form.date}
        onChange={(e) => setForm({ ...form, date: e.target.value })}
      />
      <input
        placeholder="Time Slot"
        value={form.timeSlot}
        onChange={(e) => setForm({ ...form, timeSlot: e.target.value })}
      />
      <button onClick={createBooking}>Add Booking</button>
      <ul>
        {bookings.map((b) => (
          <li key={b._id}>
            {b.name} - {b.date} - {b.timeSlot}
            <button onClick={() => deleteBooking(b._id)}>Delete</button>
            <button onClick={() => updateBooking(b._id)}>Update</button>
          </li>
        ))}
      </ul>
      <findAnnie />
    </div>
  );
}
