import React, { useState, useEffect } from "react";

export default function Booking() {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch today's booking(s)
  const fetchBookings = async (autoCreateIfEmpty = false) => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:3001/api/v1/bookings");
      const data = await res.json();

      if (data.length === 0 && autoCreateIfEmpty) {
        // No booking found for today → create one
        await createBooking();
        return;
      }

      setBookings(data);
    } catch (error) {
      console.error("Error fetching bookings:", error);
    } finally {
      setLoading(false);
    }
  };

  // Create today's booking
  const createBooking = async () => {
    try {
      const res = await fetch("http://localhost:3001/api/v1/bookings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ first: "", second: "", third: "" }),
      });

      if (!res.ok) {
        const err = await res.json();
        console.warn("Booking creation failed:", err.error);
        return;
      }

      // Fetch again after creating
      await fetchBookings(false);
    } catch (error) {
      console.error("Error creating booking:", error);
    }
  };

  // Update slot
  const updateSlot = async (id, field, value) => {
    try {
      await fetch(`http://localhost:3001/api/v1/bookings/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ [field]: value }),
      });
      await fetchBookings(false);
    } catch (error) {
      console.error("Error updating booking:", error);
    }
  };

  useEffect(() => {
    fetchBookings(true); // auto-create if empty
  }, []);

  return (
    <div>
      <h1>Today's Booking</h1>

      {loading && <p>Loading...</p>}

      {!loading && bookings.length === 0 && <p>No booking for today.</p>}

      {!loading &&
        bookings.map((booking) => (
          <table key={booking._id} border="1" style={{ marginTop: "10px" }}>
            <thead>
              <tr>
                <th>First</th>
                <th>Second</th>
                <th>Third</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>
                  {booking.first || "(empty)"}{" "}
                  <button
                    onClick={() =>
                      updateSlot(
                        booking._id,
                        "first",
                        prompt("Enter new value")
                      )
                    }
                  >
                    Edit
                  </button>
                </td>
                <td>
                  {booking.second || "(empty)"}{" "}
                  <button
                    onClick={() =>
                      updateSlot(
                        booking._id,
                        "second",
                        prompt("Enter new value")
                      )
                    }
                  >
                    Edit
                  </button>
                </td>
                <td>
                  {booking.third || "(empty)"}{" "}
                  <button
                    onClick={() =>
                      updateSlot(
                        booking._id,
                        "third",
                        prompt("Enter new value")
                      )
                    }
                  >
                    Edit
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        ))}
    </div>
  );
}
