import { useState } from "react";

export default function FindAnnieBooking() {
  const [annieBooking, setAnnieBooking] = useState(null);
  const [error, setError] = useState("");

  const findAnnie = async () => {
    try {
      // Step 1: Get all bookings
      const resAll = await fetch("http://localhost:5000/bookings");
      const allBookings = await resAll.json();

      // Step 2: Find Annie by name (case-insensitive)
      const annie = allBookings.find((b) => b.name.toLowerCase() === "annie");

      if (!annie) {
        setError("No booking found for Annie");
        setAnnieBooking(null);
        return;
      }

      // Step 3: Fetch Annie's booking by ID
      const resOne = await fetch(`http://localhost:5000/bookings/${annie._id}`);
      if (!resOne.ok) {
        throw new Error("Booking not found");
      }
      const bookingData = await resOne.json();
      setAnnieBooking(bookingData);
      setError("");
    } catch (err) {
      setError(err.message);
      setAnnieBooking(null);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <button onClick={findAnnie}>Find Annie's Booking</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {annieBooking && (
        <div style={{ marginTop: "10px" }}>
          <h3>Booking Found:</h3>
          <p>Name: {annieBooking.name}</p>
          <p>Date: {annieBooking.date}</p>
          <p>Time Slot: {annieBooking.timeSlot}</p>
        </div>
      )}
    </div>
  );
}
