import { useState, useEffect } from "react";

export default function App() {
  const [bookings, setBookings] = useState([]);
  const [form, setForm] = useState({
    date: "",
    first: "",
    second: "",
    third: "",
  });
  const [editId, setEditId] = useState(null);
  const [searchDate, setSearchDate] = useState("");
  const BASE_URL = "http://localhost:3000/api/v1/bookings";

  // Fetch all bookings
  const fetchBookings = async () => {
    try {
      const res = await fetch(BASE_URL);
      const data = await res.json();
      setBookings(data);
    } catch (err) {
      console.error("Error fetching bookings:", err);
    }
  };

  useEffect(() => {
    fetchBookings();
  }, []);

  // Create booking
  const createBooking = async () => {
    try {
      const res = await fetch(BASE_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Failed to create booking");
      await fetchBookings();
      resetForm();
    } catch (err) {
      console.error(err);
    }
  };

  // Update booking by ID
  const updateBookingById = async () => {
    try {
      const res = await fetch(`${BASE_URL}/id/${editId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Failed to update booking");
      await fetchBookings();
      resetForm();
    } catch (err) {
      console.error(err);
    }
  };

  // Update bookings by DATE
  const updateBookingByDate = async () => {
    try {
      const res = await fetch(`${BASE_URL}/date/${form.date}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Failed to update by date");
      await fetchBookings();
      resetForm();
    } catch (err) {
      console.error(err);
    }
  };

  // Delete booking by ID
  const deleteBookingById = async (id) => {
    try {
      const res = await fetch(`${BASE_URL}/id/${id}`, { method: "DELETE" });
      if (!res.ok) throw new Error("Failed to delete booking");
      await fetchBookings();
    } catch (err) {
      console.error(err);
    }
  };

  // Delete bookings by DATE
  const deleteBookingByDate = async (date) => {
    try {
      const res = await fetch(`${BASE_URL}/date/${date}`, { method: "DELETE" });
      if (!res.ok) throw new Error("Failed to delete by date");
      await fetchBookings();
    } catch (err) {
      console.error(err);
    }
  };

  // Search bookings by DATE
  const searchBookingsByDate = async () => {
    try {
      const res = await fetch(`${BASE_URL}/date/${searchDate}`);
      if (!res.ok) {
        setBookings([]);
        return;
      }
      const data = await res.json();
      setBookings(data);
    } catch (err) {
      console.error(err);
    }
  };

  // Reset form
  const resetForm = () => {
    setForm({ date: "", first: "", second: "", third: "" });
    setEditId(null);
  };

  // Handle form change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Handle edit click
  const handleEdit = (booking) => {
    setEditId(booking._id);
    setForm({
      date: booking.date ? booking.date.substring(0, 10) : "",
      first: booking.first,
      second: booking.second,
      third: booking.third,
    });
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Bookings Manager</h1>

      {/* Form for Create / Update */}
      <div style={{ marginBottom: "1rem" }}>
        <input
          type="date"
          name="date"
          value={form.date}
          onChange={handleChange}
        />
        <input
          name="first"
          placeholder="First"
          value={form.first}
          onChange={handleChange}
        />
        <input
          name="second"
          placeholder="Second"
          value={form.second}
          onChange={handleChange}
        />
        <input
          name="third"
          placeholder="Third"
          value={form.third}
          onChange={handleChange}
        />
        {editId ? (
          <>
            <button onClick={updateBookingById}>Update by ID</button>
            <button onClick={updateBookingByDate}>Update by Date</button>
            <button onClick={resetForm}>Cancel</button>
          </>
        ) : (
          <button onClick={createBooking}>Add Booking</button>
        )}
      </div>

      {/* Search by Date */}
      <div style={{ marginBottom: "1rem" }}>
        <input
          type="date"
          value={searchDate}
          onChange={(e) => setSearchDate(e.target.value)}
        />
        <button onClick={searchBookingsByDate}>Search</button>
        <button onClick={fetchBookings}>Reset</button>
      </div>

      {/* Bookings Table */}
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Date</th>
            <th>First</th>
            <th>Second</th>
            <th>Third</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((b) => (
            <tr key={b._id}>
              <td>{b.date ? b.date.substring(0, 10) : ""}</td>
              <td>{b.first}</td>
              <td>{b.second}</td>
              <td>{b.third}</td>
              <td>
                <button onClick={() => handleEdit(b)}>Edit</button>
                <button onClick={() => deleteBookingById(b._id)}>
                  Del by ID
                </button>
                <button
                  onClick={() => deleteBookingByDate(b.date?.substring(0, 10))}
                >
                  Del by Date
                </button>
              </td>
            </tr>
          ))}
          {bookings.length === 0 && (
            <tr>
              <td colSpan="5" style={{ textAlign: "center" }}>
                No bookings found
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
