import { useState, useEffect } from "react";

export default function Booking() {
  const [bookings, setBookings] = useState([]);
  const [form, setForm] = useState({ first: "", second: "", third: "" });
  const [editField, setEditField] = useState(null);
  const [editId, setEditId] = useState(null);
  const BASE_URL = "http://localhost:3001/api/v1/bookings";

  // Fetch today's bookings only
  const fetchBookings = async () => {
    try {
      const res = await fetch(`${BASE_URL}`);
      const data = await res.json();
      setBookings(data);
    } catch (err) {
      console.error("Error fetching bookings:", err);
    }
  };

  useEffect(() => {
    fetchBookings();
  }, []);

  // Create booking
  const createBooking = async () => {
    try {
      const res = await fetch(BASE_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Failed to create booking");
      await fetchBookings();
      resetForm();
    } catch (err) {
      console.error(err);
    }
  };

  // Update one field
  const updateBookingField = async (id, field, value) => {
    try {
      const res = await fetch(`${BASE_URL}/id/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ [field]: value }),
      });
      if (!res.ok) throw new Error("Failed to update booking");
      await fetchBookings();
      resetForm();
    } catch (err) {
      console.error(err);
    }
  };

  // Delete booking
  const deleteBookingById = async (id) => {
    try {
      const res = await fetch(`${BASE_URL}/id/${id}`, { method: "DELETE" });
      if (!res.ok) throw new Error("Failed to delete booking");
      await fetchBookings();
    } catch (err) {
      console.error(err);
    }
  };

  // Reset form
  const resetForm = () => {
    setForm({ first: "", second: "", third: "" });
    setEditId(null);
    setEditField(null);
  };

  // Form change handler
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Today's Bookings</h1>

      {/* Create Form */}
      <div style={{ marginBottom: "1rem" }}>
        <input
          name="first"
          placeholder="First"
          value={form.first}
          onChange={handleChange}
        />
        <input
          name="second"
          placeholder="Second"
          value={form.second}
          onChange={handleChange}
        />
        <input
          name="third"
          placeholder="Third"
          value={form.third}
          onChange={handleChange}
        />
        <button onClick={createBooking}>Add Booking</button>
      </div>

      {/* Bookings Table */}
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Date</th>
            <th>First</th>
            <th>Second</th>
            <th>Third</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((b) => (
            <tr key={b._id}>
              <td>{b.date ? b.date.substring(0, 10) : ""}</td>

              {/* First Field */}
              <td>
                {editId === b._id && editField === "first" ? (
                  <>
                    <input
                      value={form.first}
                      onChange={(e) =>
                        setForm({ ...form, first: e.target.value })
                      }
                    />
                    <button
                      onClick={() =>
                        updateBookingField(b._id, "first", form.first)
                      }
                    >
                      Save
                    </button>
                    <button onClick={resetForm}>Cancel</button>
                  </>
                ) : (
                  <>
                    {b.first}
                    <button
                      onClick={() => {
                        setEditId(b._id);
                        setEditField("first");
                        setForm({ ...form, first: b.first });
                      }}
                    >
                      Edit
                    </button>
                  </>
                )}
              </td>

              {/* Second Field */}
              <td>
                {editId === b._id && editField === "second" ? (
                  <>
                    <input
                      value={form.second}
                      onChange={(e) =>
                        setForm({ ...form, second: e.target.value })
                      }
                    />
                    <button
                      onClick={() =>
                        updateBookingField(b._id, "second", form.second)
                      }
                    >
                      Save
                    </button>
                    <button onClick={resetForm}>Cancel</button>
                  </>
                ) : (
                  <>
                    {b.second}
                    <button
                      onClick={() => {
                        setEditId(b._id);
                        setEditField("second");
                        setForm({ ...form, second: b.second });
                      }}
                    >
                      Edit
                    </button>
                  </>
                )}
              </td>

              {/* Third Field */}
              <td>
                {editId === b._id && editField === "third" ? (
                  <>
                    <input
                      value={form.third}
                      onChange={(e) =>
                        setForm({ ...form, third: e.target.value })
                      }
                    />
                    <button
                      onClick={() =>
                        updateBookingField(b._id, "third", form.third)
                      }
                    >
                      Save
                    </button>
                    <button onClick={resetForm}>Cancel</button>
                  </>
                ) : (
                  <>
                    {b.third}
                    <button
                      onClick={() => {
                        setEditId(b._id);
                        setEditField("third");
                        setForm({ ...form, third: b.third });
                      }}
                    >
                      Edit
                    </button>
                  </>
                )}
              </td>

              {/* Delete */}
              <td>
                <button onClick={() => deleteBookingById(b._id)}>Delete</button>
              </td>
            </tr>
          ))}
          {bookings.length === 0 && (
            <tr>
              <td colSpan="5" style={{ textAlign: "center" }}>
                No bookings for today
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
