import React, { useState, useEffect } from "react";

export default function Pupil() {
  const API_URL = "http://localhost:3001/api/v1/pupils"; // Your pupils endpoint
  const [pupils, setPupils] = useState([]);
  const [newPupil, setNewPupil] = useState({ name: "", class: "", year: "" });
  const [loading, setLoading] = useState(false);

  // Fetch all pupils
  const fetchPupils = async () => {
    setLoading(true);
    try {
      const res = await fetch(API_URL);
      if (!res.ok) throw new Error("Failed to fetch pupils");
      const data = await res.json();
      setPupils(data);
    } catch (err) {
      console.error("Error fetching pupils:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPupils();
  }, []);

  // Create new pupil
  const createPupil = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newPupil),
      });
      if (!res.ok) throw new Error("Failed to create pupil");
      setNewPupil({ name: "", class: "", year: "" });
      fetchPupils();
    } catch (err) {
      console.error("Error creating pupil:", err);
    }
  };

  // Update pupil field
  const updateField = async (id, field, value) => {
    try {
      const res = await fetch(`${API_URL}/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ [field]: value }),
      });
      if (!res.ok) throw new Error("Failed to update pupil");
      fetchPupils();
    } catch (err) {
      console.error("Error updating pupil:", err);
    }
  };

  // Delete pupil
  const deletePupil = async (id) => {
    try {
      const res = await fetch(`${API_URL}/${id}`, {
        method: "DELETE",
      });
      if (!res.ok) throw new Error("Failed to delete pupil");
      fetchPupils();
    } catch (err) {
      console.error("Error deleting pupil:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Pupil Records</h1>

      {/* Create Pupil Form */}
      <form onSubmit={createPupil} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Name"
          value={newPupil.name}
          onChange={(e) => setNewPupil({ ...newPupil, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Class"
          value={newPupil.class}
          onChange={(e) => setNewPupil({ ...newPupil, class: e.target.value })}
        />
        <input
          type="number"
          placeholder="Year"
          value={newPupil.year}
          onChange={(e) => setNewPupil({ ...newPupil, year: e.target.value })}
        />
        <button type="submit">Add Pupil</button>
      </form>

      {/* Pupil List */}
      {loading ? (
        <p>Loading...</p>
      ) : pupils.length === 0 ? (
        <p>No pupils found.</p>
      ) : (
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>Name</th>
              <th>Class</th>
              <th>Year</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {pupils.map((p) => (
              <tr key={p._id}>
                <td>
                  {p.name}
                  <button
                    onClick={() => {
                      const value = prompt("New Name:", p.name);
                      if (value !== null) updateField(p._id, "name", value);
                    }}
                  >
                    Edit
                  </button>
                </td>
                <td>
                  {p.class}
                  <button
                    onClick={() => {
                      const value = prompt("New Class:", p.class);
                      if (value !== null) updateField(p._id, "class", value);
                    }}
                  >
                    Edit
                  </button>
                </td>
                <td>
                  {p.year}
                  <button
                    onClick={() => {
                      const value = prompt("New Year:", p.year);
                      if (value !== null) updateField(p._id, "year", value);
                    }}
                  >
                    Edit
                  </button>
                </td>
                <td>
                  <button onClick={() => deletePupil(p._id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
