import { useState, useEffect } from "react";
import FindAnnie from "./components/FindAnnie.jsx";

export default function BookingApp() {
  const [bookings, setBookings] = useState([]);
  const [form, setForm] = useState({ name: "", date: "", timeSlot: "" });

  // Create booking
  const createBooking = async () => {
    await fetch("http://localhost:5000/bookings", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    setForm({ name: "", date: "", timeSlot: "" });
    getBookings();
  };

  // Read all bookings
  const getBookings = async () => {
    const res = await fetch("http://localhost:5000/bookings");
    const data = await res.json();
    setBookings(data);
  };

  // Update booking
  const updateBooking = async (id) => {
    await fetch(`http://localhost:5000/bookings/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    getBookings();
  };

  // Delete booking
  const deleteBooking = async (id) => {
    await fetch(`http://localhost:5000/bookings/${id}`, { method: "DELETE" });
    getBookings();
  };

  //delete booking by name
  const deleteBookingbyName = async () => {
    if (!name.trim()) {
      setMessage("Please enter a name");
      return;
    }

    try {
      const res = await fetch(`http://localhost:5000/bookings/name/${name}`, {
        method: "DELETE",
      });

      if (res.ok) {
        const data = await res.json();
        setMessage(data.message);
      } else {
        const errorData = await res.json();
        setMessage(errorData.error || "Error deleting booking");
      }
    } catch (err) {
      setMessage("Network error");
    }
  };

  useEffect(() => {
    getBookings();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Booking Time Slots</h2>
      <input
        placeholder="Name"
        value={form.name}
        onChange={(e) => setForm({ ...form, name: e.target.value })}
      />
      <input
        type="date"
        value={form.date}
        onChange={(e) => setForm({ ...form, date: e.target.value })}
      />
      <input
        placeholder="Time Slot"
        value={form.timeSlot}
        onChange={(e) => setForm({ ...form, timeSlot: e.target.value })}
      />
      <button onClick={createBooking}>Add Booking</button>
      <ul>
        {bookings.map((slot) => (
          <li key={slot.name}>
            {slot.name} - {slot.date} - {slot.timeSlot}
            <button onClick={() => deleteBooking(slot._id)}>Delete</button>
            <button onClick={() => updateBooking(slot._id)}>Update</button>
          </li>
        ))}
      </ul>
      <button onClick={() => deleteBooking((bookings.name = "New"))}>
        Delete New
      </button>
      <FindAnnie />
      Hello?
    </div>
  );
}
