import React, { useState, useEffect } from "react";
import BookingSlot from "./BookingSlot";

export default function Booking() {
  const [bookings, setBookings] = useState([]);
  const [activeSlot, setActiveSlot] = useState(null);
  const [pupils, setPupils] = useState([]);

  const timeSlots = [
    { field: "first", label: "12:00" },
    { field: "second", label: "12:10" },
    { field: "third", label: "12:20" },
    { field: "fourth", label: "12:30" },
    { field: "fifth", label: "12:40" },
    { field: "sixth", label: "12:50" },
  ];

  // fetchBookings, fetchPupils, updateSlot functions go here...

  useEffect(() => {
    fetchBookings(true);
    fetchPupils();
  }, []);

  return (
    <div>
      <h1>Today's Booking</h1>

      {bookings.map((booking) => (
        <table key={booking._id} border="1" style={{ marginTop: "10px" }}>
          <thead>
            <tr>
              {timeSlots.map((slot) => (
                <th key={slot.field}>{slot.label}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            <tr>
              {timeSlots.map((slot) => (
                <BookingSlot
                  key={slot.field}
                  booking={booking}
                  field={slot.field}
                  label={slot.label}
                  onEdit={setActiveSlot}
                />
              ))}
            </tr>
          </tbody>
        </table>
      ))}

      {activeSlot && (
        <div style={{ marginTop: "20px" }}>
          <h3>Select Pupil for {activeSlot.field} slot:</h3>
          {pupils.map((pupil) => (
            <button
              key={pupil._id}
              onClick={() =>
                updateSlot(activeSlot.id, activeSlot.field, pupil.name)
              }
              style={{ margin: "5px" }}
            >
              {pupil.name} ({pupil.class}, {pupil.year})
            </button>
          ))}
          <div>
            <button onClick={() => setActiveSlot(null)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
}
