import { useState, useEffect } from "react";

export default function App() {
  const [bookings, setBookings] = useState([]);
  const [form, setForm] = useState({ name: "", date: "", service: "" });
  const [editId, setEditId] = useState(null);
  const [searchDate, setSearchDate] = useState("");
  const BASE_URL = "http://localhost:3001/api/v1/bookings";

  // Fetch all bookings
  const fetchBookings = async () => {
    try {
      const res = await fetch(BASE_URL);
      const data = await res.json();
      setBookings(data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchBookings();
  }, []);

  // Create booking
  const createBooking = async () => {
    try {
      const res = await fetch(BASE_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Failed to create booking");
      await fetchBookings();
      setForm({ name: "", date: "", service: "" });
    } catch (err) {
      console.error(err);
    }
  };

  // Update booking by ID
  const updateBookingById = async () => {
    try {
      const res = await fetch(`${BASE_URL}/id/${editId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Failed to update booking");
      await fetchBookings();
      setEditId(null);
      setForm({ name: "", date: "", service: "" });
    } catch (err) {
      console.error(err);
    }
  };

  // Update bookings by DATE
  const updateBookingByDate = async () => {
    try {
      const res = await fetch(`${BASE_URL}/date/${form.date}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Failed to update bookings by date");
      await fetchBookings();
      setForm({ name: "", date: "", service: "" });
    } catch (err) {
      console.error(err);
    }
  };

  // Delete booking by ID
  const deleteBookingById = async (id) => {
    try {
      const res = await fetch(`${BASE_URL}/id/${id}`, { method: "DELETE" });
      if (!res.ok) throw new Error("Failed to delete booking");
      await fetchBookings();
    } catch (err) {
      console.error(err);
    }
  };

  // Delete bookings by DATE
  const deleteBookingByDate = async (date) => {
    try {
      const res = await fetch(`${BASE_URL}/date/${date}`, { method: "DELETE" });
      if (!res.ok) throw new Error("Failed to delete bookings by date");
      await fetchBookings();
    } catch (err) {
      console.error(err);
    }
  };

  // Search bookings by DATE
  const searchBookingsByDate = async () => {
    try {
      const res = await fetch(`${BASE_URL}/date/${searchDate}`);
      if (!res.ok) {
        console.warn("No bookings found for this date");
        return;
      }
      const data = await res.json();
      setBookings(data);
    } catch (err) {
      console.error(err);
    }
  };

  // Handle form changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Handle edit click
  const handleEdit = (booking) => {
    setEditId(booking._id);
    setForm({
      name: booking.name,
      date: booking.date ? booking.date.substring(0, 10) : "",
      service: booking.service,
    });
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Bookings Manager</h1>

      {/* Create / Update form */}
      <div style={{ marginBottom: "1rem" }}>
        <input
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleChange}
        />
        <input
          name="date"
          type="date"
          value={form.date}
          onChange={handleChange}
        />
        <input
          name="service"
          placeholder="Service"
          value={form.service}
          onChange={handleChange}
        />
        {editId ? (
          <>
            <button onClick={updateBookingById}>Update by ID</button>
            <button onClick={updateBookingByDate}>Update by Date</button>
          </>
        ) : (
          <button onClick={createBooking}>Add Booking</button>
        )}
      </div>

      {/* Search by date */}
      <div style={{ marginBottom: "1rem" }}>
        <input
          type="date"
          value={searchDate}
          onChange={(e) => setSearchDate(e.target.value)}
        />
        <button onClick={searchBookingsByDate}>Search</button>
        <button onClick={fetchBookings}>Reset</button>
      </div>

      {/* Bookings table */}
      <table border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Name</th>
            <th>Date</th>
            <th>Service</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((b) => (
            <tr key={b._id}>
              <td>{b.name}</td>
              <td>{b.date ? b.date.substring(0, 10) : ""}</td>
              <td>{b.service}</td>
              <td>
                <button onClick={() => handleEdit(b)}>Edit</button>
                <button onClick={() => deleteBookingById(b._id)}>
                  Del by ID
                </button>
                <button
                  onClick={() => deleteBookingByDate(b.date?.substring(0, 10))}
                >
                  Del by Date
                </button>
              </td>
            </tr>
          ))}
          {bookings.length === 0 && (
            <tr>
              <td colSpan="4" style={{ textAlign: "center" }}>
                No bookings found
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
